import React, { useState, useEffect, useRef } from 'react';
import { color, font, greenGradient } from '../../layout/main';
import Title from './title';
import { TweenMax } from 'gsap';

const images = [
  {
    img: '/static/bakery.jpg',
    msg: 'influye'
  },
  {
    img: '/static/market.jpg',
    msg: 'patrocina',
  },
  {
    img: '/static/drug.jpg',
    msg: 'beneficiate'
  }
];

const Cont = ({ img, msg }) => {

  return(
    <div className="main animated slot">
      <h1>{msg}</h1>
      <style jsx>{`
        .main{
          height: 100%;
          background: url(${img})top center no-repeat;
          background-size: cover;
        }
      `}
      </style>
    </div>
  )
}

const Carousel = () => {
  const [index, setIndex] = useState(0);
  const current = useRef(null);
  const [width, setWidth] = useState(0);
  useEffect(()=> {
    setWidth(window.innerWidth);
  },[])
  return(
    <div className="main">
    {console.log(width)}
      {index === 0 && <Cont  {...images[0]}/>}
      {index === 1 && <Cont  {...images[0]}/>}
      {index === 2 && <Cont  {...images[0]}/>}
      <style jsx>{`
        .main{
          position: absolute;
          width: 100%;
          height: 100%;
          top: 0;
          right: ${width};
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {

  return(
    <div className="main">
      <header>
        <Title word="embajadores" />
      </header>
      <div className="body">
        <Carousel />
      </div>
      <style jsx>{`
        .main{
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
        .body{
          position: relative;
          flex-grow: 1;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
