import React, { useState, useEffect } from 'react';
import { color, font } from '../../layout/main';
import uuid from 'uuid/v1';
import TweenMax from 'gsap';

const Title = () => {
  const [queque, setQueque] = useState(Array(20).fill("GREENPOST'S"));
  useEffect(()=> {
    TweenMax.set('.titleText', { x: (i) => i * 50 });
    TweenMax.to('.titleText', 2, {
      x: ()=> '+=100',
      modifiers: {
        x: function(x) {
          return x % 500; //force x value to be between 0 and 500 using modulus
        }
      },
      repeat: -1
    });
  })
  return(
    <div>
      {console.log(queque.length)}
      <ul id="text">
        {
          queque.map((word, i) => <li className="titleText" key={uuid()}>{word}</li>)
        }
      </ul>
      <style jsx >{`
        div{
            overflow: hidden;
          }
        ul{
          margin: 0;
          padding: 1rem;
          color: ${color.prim};
          display: flex;
          align-items: center;
          transition: 250ms ease;
        }
        li{
          flex-shrink: 0;
          margin: 0 1rem;
          transition: 250ms ease;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {
  return(
    <div>
      <Title />
      <style jsx>{`
        div{
          width: 100vw;
          overflow: hidden;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
