import React, { useState, useEffect } from 'react';
import { color, font } from '../../layout/main';
import uuid from 'uuid/v1';
import TweenMax from 'gsap';
import { posts as myPosts } from '../../test/posts';

const Word = ({ word, id }) => {
  useEffect(()=> {
    //TweenMax.to(`#${id}`, 2, { x: "50px", opacity: 1 });
  },[])
  return (
    <>
      <li id={id} className="animated slideInLeft">{word}</li>
      <style jsx>{`
        li{
          flex-shrink: 0;
          margin: 0 1rem;
          transition: 250ms ease;
        }
      `}
      </style>
    </>
  )
}

const Title = () => {
  const [queque, setQueque] = useState(Array(15).fill("GREENPOST'S"));
  return(
    <div className="shadow">
      <ul id="cartext">
        {
          queque.map((word, i) => <li className="cartext">{word}</li>)
        }
      </ul>
      <style jsx >{`
        ul{
          margin: 0;
          padding: 1rem;
          color: ${color.prim};
          display: flex;
          align-items: center;
          transition: 250ms ease;
          animation-name: myAni;
          animation-duration: 15s;
          animation-timing-function: ease-in-out;
          animation-iteration-count: infinite;
          transform: translateX(-100px);
        }
        li{
          flex-shrink: 0;
          margin: 0 1rem;
          transition: 250ms ease;
        }
        @keyframes myAni{
          0%{
            transform: translateX(-100px);
          }
          50%{
            transform: translateX(-3px);
          }
          100%{
            transform: translateX(-100px);
          }
        }
      `}
      </style>
    </div>
  )
}

const Post = ({ img, title, subTitle, history }) => {

  return(
    <div className="main">
      <div className="info">
        <h3>{title}</h3>
        <small>{subTitle}</small>
        <p>{history}</p>
      </div>
      <div className="image">
        <img src={img} alt=""/>
      </div>
      <style jsx>{`
        .main{
          display: flex;
          height: 100vh;
          padding: 0 5rem;
        }
        .info{
          width: 30%;
        }
        .image{
          width: 70%;
        }
        img{
          width: 100%;
          height: 100%;
          object-fit: cover;
          object-position: center;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {
  const [posts, setPosts] = useState(myPosts);
  return(
    <div>
      <Title />
      {
        posts.map(post => <Post {...post} />)
      }
      <style jsx>{`
        div{
          width: 100vw;
          overflow: hidden;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
