const Post = ({ img, title, subTitle, date, history, author }) => {
  const { fullName, perfilImg, url, } = author;

  return(
    <div className="main">
      <header>
        <div className="user">
          <img src={perfilImg} alt=""/>
          <p>{fullName}</p>
        </div>
        <div className="date">
          <p>{new Date(date).toLocaleDateString()}</p>
          <small>{new Date(date).toLocaleTimeString()}</small>
        </div>
      </header>
      <div className="body">
        <img src={img} alt=""/>
      </div>
      <footer>
        <div className="title">
          <p>{title}</p>
          <small>{subTitle}</small>
        </div>
        <p>{history}</p>
      </footer>
    </div>
  )
}

const Main = ()=> {
  const [posts, setPosts] = useState(myPosts);
  return(
    <div className="main">
      <Title word="top greenpost's" />
      {
        posts.slice(0, 3).map(post => <Post {...post}/>)
      }
      <style jsx>{`
        .main{
          //${greenBackground()};
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
      `}
      </style>
    </div>
  )
}


export default Main;
