import React, { useState, useEffect } from 'react';
import { color, font, greenGradient, greenBackground } from '../../layout/main';
import uuid from 'uuid/v1';
import Title from './title';
import { posts as myPosts } from '../../test/posts';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons';

const GreenPost = ({ title, subTitle, img, history }) => {

  return(
    <div className="main">
      <div className="image">
        <img src={img} alt=""/>
      </div>
      <div className="info">
        <header>
          <h1>title</h1>
          <p>subTitle</p>
        </header>
        <p>
          {history}
        </p>
      </div>
      <style jsx>{`
        .main{
          display: flex;
        }
        .image{
          width: 60%;
          height: 100%;
        }
        img{
          width: 100%;
          height: 100%;
          object-fit: cover;
          object-position: center;
        }
        .info{
          background: #fff;
          width: 40%;
        }
      `}</style>
    </div>
  )
}

const Gallery = () => {
  const [posts, setPosts] = useState(myPosts);
  return(
    <div className="main">
      <div className="cont shadow">
        <button className="control">
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>
        <GreenPost {...posts[0]} />
        <button className="control">
          <FontAwesomeIcon icon={faArrowRight} />
        </button>
      </div>
      <style jsx>{`
        .main{
          height: 100%;
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .cont{
          width: 80%;
          height: 70%;
          background: transparent;
          display: flex;
        }
        .control{
          width: 10%;
          height: 100%;
          background: transparent;
          border: none;
          transition: 250ms esae;
          color: #fff;
        }
        .control:focus{
          outline: none;
        }
        .control:hover{
          background: ${color.prim};
        }
      `}
      </style>
    </div>
  )
}

const Main = ()=> {

  return(
    <div className="main">
      <Title word="top greenpost's" />
      <Gallery />
      <style jsx>{`
        .main{
          //${greenBackground()};
          background: ${greenGradient()};
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
      `}
      </style>
    </div>
  )
}


export default Main;
