const Post = ({ img, title, subTitle, date, history, author }) => {
  const { name, lastName, perfilImg, url, } = author;

  return(
    <div className="main">
      <header>
        <div className="user">
          <img src={perfilImg} alt=""/>
          <p>{`${name} ${lastName}`}</p>
        </div>
        <div className="date">
          <p>{new Date(date).toLocaleDateString()}</p>
          <small>{new Date(date).toLocaleTimeString()}</small>
        </div>
      </header>
      <div className="body">
        <img src={img} alt=""/>
      </div>
      <footer>
        <div className="title">
          <p>{title}</p>
          <small>{subTitle}</small>
        </div>
        <p>{history}</p>
      </footer>
      <style jsx>{`
        .main{
          display: flex;
          flex-direction: column;
          width: 30%;
          height: 60vh;
        }
        header{
          display: flex;
          justify-content: space-between;
          align-items: center;
          //color: #fff;
        }
        header .user{
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        header .user img{
          width: 50px;
          height: 50px;
          border-radius: 50%;
          object-fit: cover;
          object-position: center;
        }
        header .user p{
          margin: 0;
        }
        .body{
          flex-grow: 1;
        }
        .body img{
          width: 100%;
          height:100%;
          object-fit: cover;
          object-position: center;
        }
      `}
      </style>
    </div>
  )
}

const Main = ()=> {
  const [posts, setPosts] = useState(myPosts);
  return(
    <div className="main">
      <Title word="top greenpost's" />
      {
        posts.slice(0, 3).map(post => <Post {...post}/>)
      }
      <style jsx>{`
        .main{
          //${greenBackground()};
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
      `}
      </style>
    </div>
  )
}


export default Main;
