import React, { useState, useEffect } from 'react';
import { color, font, themeBlue, themeRed } from '../../layout/main';
import Title from './title';

const Main = () => {

  return(
    <div className="main">
      <Title word="eventos" />
      <ul className="events">
        <li className="tree">
          <header>
            <h2>siembra</h2>
            <p>Regalale vida al planeta.</p>
          </header>
          <button>participar</button>
        </li>
        <li className="cold">
        <header>
          <h2>ayuda</h2>
          <p>Las zonas andinas te necesitan.</p>
        </header>
        <button>participar</button>
        </li>
        <li className="angel">
        <header>
          <h2>adopta</h2>
          <p>Un angel llega en cualquier forma.</p>
        </header>
        <button>participar</button>
        </li>
      </ul>
      <style jsx>{`
        .main{
          height: 80vh;
          display: flex;
          flex-direction: column;
          background: linear-gradient(rgba(0, 0, 0, .1), #fff);
        }
        .events{
          list-style: none;
          margin: 0;
          padding: 0;
          flex-grow: 1;
          padding: 2rem;
          display: flex;
          justify-content: space-between;
        }
        li{
          height: 100%;
          width: 30%;
          box-shadow: 5px 5px 15px rgba(0, 0, 0, .5);
          position: relative;
        }
        li header{
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          padding: .5rem;
          color: #fff;
        }
        button{
          font-size: 1.5rem;
          background: transparent;
          border: 2px solid #fff;
          color: #fff;
          position: absolute;
          bottom: .5rem;
          right: .5rem;
          text-transform: uppercase;
        }
        button:focus{
          outline: none;
        }
        h2{
          text-transform: uppercase;
          font-family: ${font.title};
        }
        .tree{
          background: url(/static/arbol.jpg);
          background-size: cover;
          background-position: bottom center;
          background-repeat: no-repeat;
        }
        .tree h2, p{
          background: ${color.prim};
        }
        .tree button:hover{
          background: ${color.prim};
          border-color: ${color.prim};
        }
        .cold{
          background: url(/static/calido.jpg);
          background-size: cover;
          background-position: bottom center;
          background-repeat: no-repeat;
        }
        .angel{
          background: url(/static/angel.jpg);
          background-size: cover;
          background-position: top center;
          background-repeat: no-repeat;
        }
      `}
      </style>
    </div>
  )
}


export default Main;
