import React, { useState, useEffect, useRef } from 'react';
import { color, font, greenGradient } from '../../layout/main';
import Title from './title';
import { TweenMax } from 'gsap';

const images = [
  {
    img: '/static/bakery.jpg',
    msg: 'influye'
  },
  {
    img: '/static/market.jpg',
    msg: 'patrocina',
  },
  {
    img: '/static/drug.jpg',
    msg: 'beneficiate'
  }
];

const Cont = ({ img, msg, id, className }) => {

  return(
    <div className={`main animated ${className}`}>
      <h1>{msg}</h1>
      <style jsx>{`
        .main{
          height: 100%;
          background: url(${img})top center no-repeat;
          background-size: cover;
        }
      `}
      </style>
    </div>
  )
}

const Carousel = () => {
  const [index, setIndex] = useState(0);
  const current = useRef(null);
  const [width, setWidth] = useState(0);
  return(
    <div className="main">
    {console.log(width)}
      <Cont id="slot-1" className="slideInRight"  {...images[0]}/>
      <Cont id="slot-2" className=""  {...images[1]}/>
      <Cont id="slot-3" className=""  {...images[2]}/>
      <style jsx>{`
        .main{
          position: absolute;
          width: 100%;
          height: 100%;
          top: 0;
          right: -100%;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {

  return(
    <div className="main">
      <header>
        <Title word="embajadores" />
      </header>
      <div className="body">
        <Carousel />
      </div>
      <style jsx>{`
        .main{
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
        .body{
          position: relative;
          flex-grow: 1;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
