import React, { useState, useRef, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ON_STORE } from '../../store/actions';
import './wish.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGift } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const Wish = () => {
  const wish = useSelector(state => state.user.greenPost.wish);
  const user = useSelector(state => state.user);
  const [tip, setTip] = useState(0);
  const dispatch = useDispatch();
  wish.found = 20;
  const [found, setFound] = useState(wish.found);
  const inputTip = useRef();

  user.owner = true;
  const onSubmit = async(e) => {
    try{
      e.preventDefault();
      const data = {
        tip,
        targetId: user._id,
      }
        //setFound(wish.found + tip);
        console.log(typeof(found))
      //const res = await axios.post('/greenpost/tip', data);
      //console.log(res.data);
    }catch(err){
      console.log(err);
    }
  }
  return(
    <div className="wish_main_cont animated fadeIn">
      {console.log(found)}
      {
        Object.keys(wish).length < 0 && (
          <div className="nowish_main_cont">
            <button onClick={()=> dispatch({ type: ON_STORE })} disabled={!user.owner}>
              <FontAwesomeIcon icon={faGift} />
              {
                user.owner
                  ? <p>Elige un deseo, podria concederse muy pronto.</p>
                  : <p>{user.name} aun no a elegido un deseo</p>
              }
            </button>
          </div>
        )
      }
      {
        Object.keys(wish).length > 0 && (
          <div className="wish_cont shadow">
            <img src={wish.img} alt=""/>
            <form onSubmit={onSubmit}>
              <input value={tip > wish.price - found ? wish.price - found : tip} onChange={(e)=> setTip(e.currentTarget.value)} type="number" />
              <button type="submit">enviar</button>
            </form>
            <div className="wish_loader_cont">
              <div
                className="wish_loader"
                style={{ width: `${(found * 100)/wish.price}%` }}
              />
            </div>
            <footer>
              <p>{wish.name}</p>
              <p>${wish.price}</p>
            </footer>
          </div>
        )
      }
    </div>
  );
}

export default Wish;
