import React, { useState, useEffect, useRef, useReducer } from 'react';
import Layout from '../components/main/layout';
import Hero from '../components/main/hero';
//import GreenPost from '../components/main/greenpost';
//import MainSection from '../components/main/main';
//import Embajadores from '../components/main/embajador';
import uuid from 'uuid/v1';

const images = [
  {
    img: '/static/bakery.jpg',
    msg: 'influye'
  },
  {
    img: '/static/market.jpg',
    msg: 'auspicia',
  },
  {
    img: '/static/drug.jpg',
    msg: 'beneficiate'
  }
];

const Cont = ({msg, img, index}) => {
  return(
    <div className="main animated slideInRight">
        <h1>{msg}</h1>
      <style jsx>{`
        .main{
          background: url(${img})top center no-repeat;
          background-size: cover;
          /*display: flex;
          justify-content: center;
          align-items: center;*/
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }
        h1{
          color: #fff;
          font-size: 8rem;
          text-transform: uppercase;
        }
      `}
      </style>
    </div>
  )
}


const Carousel = () => {
  const [current, setCurrent] = useState(0);
  const [visible, setVisible] = useReducer((state, next) => ({...state, ...next}), {
    bakery: true,
    market: false,
    drug: false,
  });
  const bakeryRef = useRef(null);
  const marketRef = useRef(null);
  const drugRef = useRef(null);

  useEffect(()=> {
    const next = (current + 1) % images.length;
    const id = setTimeout(()=> setCurrent(next), 5000);
    return ()=> clearTimeout(id);
  },[current])
  const { bakery, market, drug } = visible;
  return(
    <div className="main">
      { bakery && <Cont ref={bakeryRef} {...images[current]} /> }
      { market && <Cont ref={marketRef} {...images[current]} /> }
      { drug && <Cont ref={drugRef} {...images[current]} /> }
      <style jsx>{`
        .main{
          height: 100vh;
          overflow: hidden;
          display: flex;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {

  return(
    <Layout>
      <Hero />
      <Carousel />
    </Layout>
  )
}

export default Main;
