import React, { useEffect, useState } from 'react';
import './floating-notes.scss';
import io from 'socket.io-client';
import MyLink from '../mylink/link';

const notification = {
  type: 'Reaccion',
  msg: 'Le gusta tu post',
  user: {
    perfilImg: '/static/random/r2.jpg',
    fullName: 'Green Star',
    id: '123456789',
  }
}

const Note = ({ note }) => {
  const { type, user, msg } = note;
  return(
    <div className="floating_note_cont">
      <header>
        <p>{note.type}</p>
        <button>x</button>
      </header>
      <img src={user.perfilImg} alt=""/>
      <MyLink id={user.id}>{user.fullName}</MyLink>
      <p>{msg}</p>
      <style jsx>{`
        div{
          height: 100px;
          background: #fff;
          position: relative;
          left: 100%;
          margin: .5rem;
          z-index: 50;
          border-radius: 3px;
        }
        header{
          padding: .2rem;
          display: flex;
          justify-content: space-between;
          align-items: center;
          background: #8bb940;
        }
        header p{
          margin: 0;
        }
        header button{
          background: transparent;
          transition: 250ms ease;
          border: none;
          color: #333;
        }
        header button:focus{
          outline: none;
        }
        header button:hover{
          color: #fff;
        }
        img{
          width: 60px;
          height: 60px;
          border-radius: 50%;
          object-fit: cover;
          object-position: center;
        }
      `}
      </style>
    </div>
  )
}

const FloatingNotes = () => {

  return(
    <div className="floating_main_cont">
      <Note note={notification} />
    <style jsx>{`
      .floating_main_cont{
        position: fixed;
        top: 0;
        left: -30%;
        border: 1px solid red;
        width: 30%;
        height: 100vh;
        display: flex;
        flex-direction: column;
        justify-content: flex-end;
        z-index: 50;
      }
    `}
    </style>
    </div>
  )
}

export default FloatingNotes;
