const CommonFriends = () => {
  const [friends, setFriends] = useState([]);
  const tid = useSelector(state => state.user._id);
  const onFriends = async() => {
    try {
      const res = await axios.get(`/friend/commonFriends/${tid}`);
      setFriends(res.data);
    } catch (e) {
      console.log(e);
    }
  }

  useEffect(()=> {
    onFriends();
  },[tid])

  return(
    <ul>
      {
        friends.map(friend => <Friend key={uuid()} {...friend} />)
      }
      <style jsx>{`
        ul{
          list-style: none;
          width: 100%;
          height: 40vh;
          display: flex;
          margin: 0;
          padding: 0;
          padding-top: 1rem;
          overflow: hidden;
          flex-wrap: no-wrap;
          transition: 250ms ease;
        }
      `}
      </style>
    </ul>
  )
}

