import Rect from 'react';
import { color, font } from '../../layout/main';

const Embajadores = () => {

  return(
    <div className="main">
      <div className="info">
        <h1>salva el planeta</h1>
        <p>Participa en nuestros eventos, auspicia negocios locales, recibe regalos especiales. Se uno de nuestros embajadores.</p>
      </div>
      <div className="woman">
        <button>Registro</button>
        <img src="/static/cusco.png" alt=""/>
      <div className="circle">
        <div className="inner_circle">
          <div className="inner_circle"></div>
        </div>
      </div>
      </div>
    <style jsx>{`
      .main{
        display: flex;
        width: 100%;
        height: 60vh;
        background: linear-gradient(rgba(0, 0, 0, .1), #fff);
        position: relative;
      }
      .circle{
        display: flex;
        justify-content: center;
        align-items: center;
        width: 65vh;
        height: 65vh;
        border-radius: 50%;
        border: 1px solid rgba(0, 0, 0, .2);
        position: absolute;
        top: 0;
        left: 33%;
        z-index: -1;
      }
      .inner_circle{
        display: flex;
        justify-content: center;
        align-items: center;
        height: 50vh;
        width: 50vh;
        border-radius: 50%;
        border: 1px solid rgba(0, 0, 0, .2);
      }
      .inner_circle div{
        width: 30vh;
        height: 30vh;
      }
      .info{
        flex-grow: 1;
        //border: 1px solid red;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        position: relative;
      }
      .info h1{
        text-transform: uppercase;
        font-family: ${font.title};
        margin: 0;
        font-size: 5rem;
        width: 60%;
        color: ${color.dark};
        background: url(/static/cusco.jpg) no-repeat right;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
      }
      .info p{
        width: 60%;
      }
      img{
        width: 100%;
        height: 100%;
        object-fit: cover;
        object-position: left center;
      }
      .woman{
        width: 50%;
        justify-content: flex-end;
        position: relative;
      }
      .woman button{
        position: absolute;
        right: 1rem;
        bottom: 1rem;
        transition: 250ms ease;
        background: transparent;
        border: 2px solid ${color.light};
        color: ${color.light};
        font-size: 1.5rem;
      }
      .woman button:focus{
        outline: none;
      }
      .woman button:hover{
        background: ${color.light};
        border-color: ${color.prim};
        color: ${color.prim};
      }
    `}
    </style>
    </div>
  )
}

export default Embajadores;
