import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ON_STORE } from '../../store/actions';
import './wish.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGift } from '@fortawesome/free-solid-svg-icons';

const Wish = () => {
  const wish = useSelector(state => state.user.greenPost.wish);
  const user = useSelector(state => state.user);
  const [found, setFound] = useState(0);
  const dispatch = useDispatch();
  user.owner = true;
  return(
    <div className="wish_main_cont animated fadeIn">
      {console.log(wish)}
      {
        Object.keys(wish).length < 0 && (
          <div className="nowish_main_cont">
            <button onClick={()=> dispatch({ type: ON_STORE })} disabled={!user.owner}>
              <FontAwesomeIcon icon={faGift} />
              {
                user.owner
                  ? <p>Elige un deseo, podria concederse muy pronto.</p>
                  : <p>{user.name} aun no a elegido un deseo</p>
              }
            </button>
          </div>
        )
      }
      {
        Object.keys(wish).length > 0 && (
          <div className="wish_cont shadow">
            <img src={wish.img} alt=""/>
            <form>
              <input value={found > wish.price ? wish.price : found} onChange={(e)=> setFound(e.currentTarget.value)} type="number" max={wish.price}/>
              <button>enviar</button>
            </form>
            <div className="wish_loader_cont">
              <div
                className="wish_loader"
              />
            </div>
            <footer>
              <p>{wish.name}</p>
              <p>${wish.price}</p>
            </footer>
          </div>
        )
      }
    </div>
  );
}

export default Wish;
