import React, { useState, useEffect } from 'react';
import Layout from '../components/main/layout';
import Hero from '../components/main/hero';
//import GreenPost from '../components/main/greenpost';
//import MainSection from '../components/main/main';
//import Embajadores from '../components/main/embajador';
import uuid from 'uuid/v1';

const images = [
  {
    img: '/static/bakery.jpg',
    msg: 'influye'
  },
  {
    img: '/static/market.jpg',
    msg: 'auspicia',
  },
  {
    img: '/static/drug.jpg',
    msg: 'beneficiate'
  }
];

const Cont = ({msg, img, index}) => {
  const [current, setCurrent] = useState(0);

  useEffect(()=> {
    console.log(current);
    const next = (current + 1) % images.length;
    const id = setTimeout(()=> setCurrent(next), 5000);
    return ()=> clearTimeout(id);
  },[current])
  return(
    <div className="main">
        <h1>{msg}</h1>
      <style jsx>{`
        .main{
          background: url(${img})top center no-repeat;
          background-size: cover;
          display: flex;
          justify-content: center;
          align-items: center;
        }
        h1{
          color: #fff;
          font-size: 8rem;
          text-transform: uppercase;
        }
      `}
      </style>
    </div>
  )
}


const Carousel = () => {

  return(
    <div className="main">

      <style jsx>{`
        .main{
          height: 100vh;
          overflow: hidden;
          display: flex;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {

  return(
    <Layout>
      <Hero />
      <Carousel />
    </Layout>
  )
}

export default Main;
