import React, { useState, useEffect } from 'react';
import { color, font } from '../../layout/main';
import uuid from 'uuid/v1';
import TweenMax from 'gsap';

const Title = () => {
  const [queque, setQueque] = useState(Array(50).fill("GREENPOST'S"));
  useEffect(()=> {
    TweenMax.to('#text', 10, { x: '50%', repeat: 'infinite' });
  },[])
  return(
    <div>
      <ul id="text">
        {
          queque.map((word, i) => <li key={uuid()}>{word}</li>)
        }
      </ul>
      <style jsx >{`
        div{
            overflow: hidden;
          }
        ul{
          margin: 0;
          padding: 1rem;
          color: ${color.prim};
          display: flex;
          align-items: center;
        }
        li{
          flex-shrink: 0;
          margin: 0 1rem;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {
  return(
    <div>
      <Title />
      <style jsx>{`
        div{
          width: 100vw;
          overflow: hidden;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
