import React, { useState, useEffect } from 'react';
import { color, font, greenGradient, greenBackground } from '../../layout/main';
import uuid from 'uuid/v1';
import Title from './title';
import { posts as myPosts } from '../../test/posts';

const GreenPost = ({ title, subTitle, img, history }) => {

  return(
    <div className="main">
      <div className="info">
        <header>
          <h1>title</h1>
          <p>subTitle</p>
        </header>
        <p>
          {history}
        </p>
      </div>
      <style jsx>{`

      `}</style>
    </div>
  )
}

const Gallery = () => {
  const [posts, setPosts] = useState(myPosts);
  return(
    <div className="main">
      <div className="cont shadow">
        <div className="control"></div>
        <GreenPost {...posts[0]} />
        <div className="control"></div>
      </div>
      <style jsx>{`
        .main{
          height: 100%;
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .cont{
          width: 80%;
          height: 70%;
          background: transparent;
        }
      `}
      </style>
    </div>
  )
}

const Main = ()=> {

  return(
    <div className="main">
      <Title word="top greenpost's" />
      <Gallery />
      <style jsx>{`
        .main{
          ${greenBackground()};
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
      `}
      </style>
    </div>
  )
}


export default Main;
