import React, { useState, useEffect, useRef, useReducer } from 'react';
import { color, font, greenGradient } from '../../layout/main';
import Title from './title';
import { TweenMax } from 'gsap';

const images = [
  {
    img: '/static/bakery.jpg',
    msg: 'influye'
  },
  {
    img: '/static/market.jpg',
    msg: 'patrocina',
  },
  {
    img: '/static/drug.jpg',
    msg: 'beneficiate'
  }
];

const Cont = ({ img, msg, id }) => {

  return(
    <div id={id} className="main animated slideInRight">
      <h1>{msg}</h1>
      <style jsx>{`
        .main{
          height: 100%;
          background: url(${img})top center no-repeat;
          background-size: cover;
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
        }
      `}
      </style>
    </div>
  )
}

const Carousel = () => {
  const [index, setIndex] = useState(0);
  const current = useRef(null);
  const [width, setWidth] = useState(0);
  const [visible, setVisible] = useReducer((state, next) => ({ ...state, ...next }),{
    bakery: true,
    market: false,
    drug: false,
  })
  useEffect(()=> {
    let i = 0;
    const interval = setInterval(function () {
      if(i === 0){
        document.getElementById('bakery').classList.remove('slideInRight');
        document.getElementById('bakery').classList += ' fadeOutLeft';
        setVisible({ market: true })
        //setTimeout(()=> , 1000);
      }
      setIndex(index + 1);
      console.log(index);
    }, 5000);
    return ()=> clearInterval(interval);
  },[visible])
  const { bakery, market, drug } = visible;
  return(
    <div className="main">
    {console.log(visible, market)}
      { bakery && <Cont id="bakery" {...images[0]} /> }
      { market && <Cont id="market" {...images[1]} /> }
      { drug && <Cont id="drug" {...images[2]} /> }
      <button onClick={()=> setVisible({ market: true, bakery: false })}>foo</button>
      <style jsx>{`
        .main{
          position: absolute;
          width: 100%;
          height: 100%;
          top: 0;
          left: 0;
        }
        button{
          position: absolute;
          z-index: 600;
          top: 0;
          left: 0;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {

  return(
    <div className="main">
      <header>
        <Title word="embajadores" />
      </header>
      <div className="body">
        <Carousel />
      </div>
      <style jsx>{`
        .main{
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
        .body{
          position: relative;
          flex-grow: 1;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
