const Post = ({ img, title, subTitle, date, history, author }) => {
  const { name, lastName, perfilImg, url, } = author;

  return(
    <div className="main shadow">
      <header>
        <div className="user">
          <img src={perfilImg} alt=""/>
          <p>{`${name} ${lastName}`}</p>
        </div>
        <div className="date">
          <p>{new Date(date).toLocaleDateString()}</p>
          <small>{new Date(date).toLocaleTimeString()}</small>
        </div>
      </header>
      <div className="body">
        <img src={img} alt=""/>
      </div>
      <footer>
        <div className="title">
          <p>{title}</p>
          <small>{subTitle}</small>
        </div>
        <ul className="info">
          <li>
            <FontAwesomeIcon icon={faHeart} />
            + 0
          </li>
          <li>
            <FontAwesomeIcon icon={faGift} />
            + 0
          </li>
          <li>
            <FontAwesomeIcon icon={faComment} />
            + 0
          </li>
        </ul>
      </footer>
      <style jsx>{`
        .main{
          display: flex;
          flex-direction: column;
          width: 100%;
          height: 60vh;
        }
        header{
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: .5rem;
          //color: #fff;
        }
        header .user{
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        header .user img{
          width: 50px;
          height: 50px;
          border-radius: 50%;
          object-fit: cover;
          object-position: center;
        }
        header .user p{
          margin: 0;
        }
        header p{
          margin: 0;
        }
        .body{
          flex-grow: 1;
        }
        .body img{
          width: 100%;
          height:100%;
          object-fit: cover;
          object-position: center;
        }
        footer{
          display: flex;
          align-items: center;
          padding: .5rem;
        }
        footer p{
          margin: 0;
          line-height: .5rem;
        }
        footer .title{
          display: flex;
          flex-direction: column;
          justify-content: flex-start;
        }
        footer .info{
          list-style: none;
          margin: 0;
          padding: 0;
          display: flex;
          justify-content: flex-end;
        }
      `}
      </style>
    </div>
  )
}

const Main = ()=> {
  const [posts, setPosts] = useState(myPosts);
  return(
    <div className="main">
      <Title word="top greenpost's" />
      <ul className="body">
        {
          posts.slice(0, 3).map(post => <li><Post key={uuid()} {...post}/></li>)
        }
      </ul>
      <style jsx>{`
        .main{
          //${greenBackground()};
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
        .body{
          flex-grow: 1;
          display: flex;
          justify-content: space-around;
          align-items: center;
          list-style: none;
          margin: 0;
          padding: 0;
        }
        li{
          width: 30%;
        }
        li:nth-child(2){
          //margin: 1rem;
        }
      `}
      </style>
    </div>
  )
}


export default Main;
