    <div className="main">
      {index === 0 || 1 && <Cont id={`slot-${index}`}  {...images[0]}/>}
      {index === 1 || 2 && <Cont id={`slot-${index}`} {...images[1]}/>}
      {index === 2 || 0 && <Cont id={`slot-${index}`} {...images[2]}/>}
      <style jsx>{`
        .main{
          position: absolute;
          width: 100%;
          height: 100%;
          top: 0;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {

  return(
    <div className="main">
      <header>
        <Title word="embajadores" />
      </header>
      <div className="body">
        <Carousel />
      </div>
      <style jsx>{`
        .main{
          height: 100vh;
          display: flex;
          flex-direction: column;
        }
        .body{
          position: relative;
          flex-grow: 1;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
