import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ON_GREEN_CREATOR } from '../store/actions';
import { Modal } from 'reactstrap';
import { color } from '../layout/var';
import '../node_modules/bootstrap/scss/bootstrap.scss';

const Creator = () => {
  const visible = useSelector(state => state.greenpost.creator);
  const [image, setImage] = useState('/static/random/r16.jpg');
  const dispatch = useDispatch();
  return(
    <Modal style={{ minWidth: '80%' }} isOpen={visible}>
      <div className="main">
        <div className="img">
          <h1>hi</h1>
        </div>
        <div className="history">
          <header>
            <h1>Historia</h1>
            <small>cuentanos tu historía!</small>
          </header>
          <form>
            <textarea />
            <button type="submit">Crear</button>
          </form>
        </div>
        <button onClick={()=>dispatch({ type: ON_GREEN_CREATOR })} title="Cancelar" className="btn_close" type="button">X</button>
      </div>
      <style jsx>{`
        .main{
          height: 60vh;
          display: flex;
          position: relative;
        }
        .img{
          width: 60%;
          background-image: url(${image});
          background-size: cover;
          background-position: center;
          background-repeat: no-repeat;
        }
        .history{
          width: 40%;
          height: 100%;
          padding: 1rem .5rem 0;
          color: ${color.prim};
        }
        h1{
          margin: 0;
          line-height: 1.5rem;
        }
        small{
          color: ${color.dark};
        }
        form{
          height: 100%;
          display: flex;
          flex-direction: column;
        }
        .btn_close{
          position: absolute;
          top: .2rem;
          right: .2rem;
          background: transparent;
          border: none;
          transition: 250ms ease;
          border: 1px solid ${color.dark};
          border-radius: 50%;
          width: 30px;
          height: 30px;
        }
        .btn_close:focus{
          outline: none;
        }
        .btn_close:hover{
          color: ${color.light};
          background: ${color.prim};
          border-color: ${color.light};
        }

      `}
      </style>
    </Modal>
  )
};

const Lab = () => {
  const dispatch = useDispatch();
  return(
    <>
      <button onClick={()=> dispatch({ type: ON_GREEN_CREATOR })}>open</button>
      <Creator />
    </>
  )
};

export default Lab;
