import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { ON_MAIN_THEME } from '../../store/actions';
import { color, font } from '../../layout/main';
import { TweenMax } from 'gsap';
import { themeGreen, themeRed, themeBlue } from '../../layout/main';
import Link from 'next/link';

const Nav = ({ prim, sec, img, index, onStop }) => {
  const dispatch = useDispatch();
  const onTheme = (index) => {
    onStop(true);
    dispatch({ type: ON_MAIN_THEME, index });
    setTimeout(()=> onStop(false), 15000);
  }
  return(
    <nav>
      <div className="line" />
      <div className="nav">
        <button onClick={()=> onTheme(0)}>1</button>
        <button onClick={()=> onTheme(1)}>2</button>
        <button onClick={()=> onTheme(2)}>3</button>
      </div>
      <div className="line" />
      <img id="logo" src="/static/logo2.png" alt=""/>
      <style jsx>{`
        nav{
          position: absolute;
          top: 0;
          right: 0;
          height: 100vh;
          width: 5%;
          min-width: 50px;
          background: ${prim};
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          z-index: 100;
        }
        .line{
          width: 2px;
          height: 25%;
          background: #fff;
          content: " ";
        }
        img#logo{
          position: absolute;
          bottom: .5rem;;
          width: 50px;
          height: 50px;
        }
        div.nav{
          margin: 2rem 0;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }
        button{
          width: 25px;
          height: 25px;
          border-radius: 50%;
          border: 1px solid #fff;
          display: flex;
          justify-content: center;
          align-items: center;
          background: transparent;
          color: #fff;
          transition: 250ms ease;
          cursor: none;
        }
        button:nth-child(1){
          background: ${index === 1 ? '#fff' : 'transparent'};
