import React, { useState, useEffect } from 'react';
import { color, font } from '../../layout/main';
import uuid from 'uuid/v1';
import TweenMax from 'gsap';

const Title = () => {
  const [queque, setQueque] = useState([]);
  //Array(50).fill("GREENPOST'S")
  /*useEffect(()=> {
    TweenMax.to('#text', 10, { x: '100%', repeat: 'infinite' });
  },[])*/
  /*const word = "GREENPOST'S";
  const queque = Array(50).fill(word);*/
/*  useEffect(()=>{
    const interval = setInterval(() => {
      const word = "GREENPOST'S";
      setQueque([word, ...queque])    ;
    }, 1000);
    return ()=> clearInterval(interval);
  },[queque])*/

  return(
    <div>
      {console.log(queque.length)}
      <ul id="text">
        {
          queque.map((word, i) => <li key={uuid()}>{word}</li>)
        }
      </ul>
      <style jsx >{`
        div{
            overflow: hidden;
          }
        ul{
          margin: 0;
          padding: 1rem;
          color: ${color.prim};
          display: flex;
          align-items: center;
          transition: 250ms ease;
        }
        li{
          flex-shrink: 0;
          margin: 0 1rem;
          transition: 250ms ease;
        }
      `}
      </style>
    </div>
  )
}

const Main = () => {
  return(
    <div>
      <Title />
      <style jsx>{`
        div{
          width: 100vw;
          overflow: hidden;
        }
      `}
      </style>
    </div>
  )
}

export default Main;
